import React from 'react'
import Axios from 'axios'
import { Progress } from 'antd';
import {connect} from 'react-redux'
var querystring = require('querystring');
class Xht extends React.Component {
    constructor(props){
        super(props);
        this.props = props;
        this.state = {
            arr : []
           
        }
    }
    godetail(id,title,type,amount){
        // console.log(id,title)
        var data = {id:id ,title:title,type:type,amount:amount};
        this.props.history.push('/detail');
        this.props.changeXq(data);

        
        
    }
    getListInfo(){
        Axios.post('https://m.souyidai.com/wap/1.6/bid/hulilist', querystring.stringify({huliProductType:'P2P',
        subIndex: 'dqb',
        pageNo: 1,
        orderBy: 'DEFAULT'}))
        .then((res)=>{
            // console.log(res.data.data);
            this.setState({
                arr : res.data.data 
             
            })

        }).catch((err)=>{
          console.log(err);
        })
    }
    componentDidMount(){
        this.getListInfo();
        this.props. changeIndex(1); //路由变化控制Nav切换
    }
    render() {
        return (          
            <div>
            <div data-v-44342678="">
                <div data-v-44342678="" className="loading-boxs small">
                    <div data-v-44342678="" className="loading cf type2">
                        <img data-v-44342678="" src="data:image/png;base64,"
                            alt="" className="loading-img"/>
                        <p data-v-44342678="">搜易贷</p>
                    </div>
                    <div data-v-44342678="" className="loading errorInfo"></div>
                    <p data-v-44342678="" className="errorText">数据加载出错，请点击重新加载</p>
                </div>
            </div>
        
            <div className="foxM-invest-main" style={{marginTop:'1.18rem'}}>
                <ul className="pro-tabs column-3 new_2_6_2">
                    <li className=""><a>默认</a></li>
                    <li className="sort sort-top"><a>参考年化结算利率</a></li>
                    <li className="sort"><a>期限</a></li>
                </ul>
                <ul className="foxM-info-group column-3"  style={{marginTop:'-0.2rem'}}>
                    {
                                    (()=>{
                                        let html = [];
                                        if(this.state.arr.length==0){return(
                                        
                                                <div className="clearInfo">
                                                <p>更多优质项目,敬请期待</p>
                                                </div>
                                               
                          
                                        )};

                                        html=this.state.arr.map((item,index)=>{
                                            return (
                                                <li data-v-0754464a="" className="btLine" style={{marginTop:'0.26rem'}} key={index} onClick={this.godetail.bind(this,item.id,item.title,item.productType,item.amount)}>
                                    <div data-v-0754464a="" className="title" style={{display:"flex",justifyContent:'space-between',height:'0.93rem',lineHeight:'0.93rem',fontSize:'0.32rem',color:'#666',marginTop:'-0.3rem',borderBottom:'1px solid  #efefef'}} >
                                        <h2 data-v-0754464a="" className="name">{item.detailTitle}</h2>
                                        <p data-v-0754464a="" className="btline-rt" style={{display:item.canBidAmount==0?"none":"block"}}>可投{(item.canBidAmount/100).toFixed(2)}元</p>
                                    </div>
                                        
                                    <div data-v-0754464a="" className="main-info" style={{marginTop:'.3rem'}}>
                                        <div data-v-0754464a="" className="group-lt">
                                            <span data-v-0754464a="" className="foxM-home-rate">{item.interestRate/100}%</span>
                                            <span data-v-0754464a="" className="font-gray">{item.interestType}</span>
                                        </div>
                                        <div data-v-0754464a="" className="group-md">
                                            <div data-v-0754464a="" className="groupL-tp">
                                                <span data-v-0754464a="" className="type-1">{item.periods}</span> 
                                                <span data-v-0754464a="" className="type-2">{item.repayMode}</span>
                                            </div>
                                            <div data-v-0754464a="" className="groupL-bt"></div>
                                        </div>
                                        <div data-v-0754464a="" className="group-rt canvas ">
                                        <Progress type="circle" percent={item.percent/100} format={() => item.statusText} width={50} strokeColor={'orange'} />
                
                                        </div>
                                    </div>
                                </li>
                                            
                                            )
                                        })
                                        return html ;

                                    })()
                     }


                </ul>
        
            </div>
        </div>
        
        )
    }

}

  export default connect((state) => {
    // console.log(state)
    return state
  },(dispatch) => {
    return {
          changeIndex(idx){
            // console.log(idx)
            dispatch({
              type : 'toggleNavIndex',
              index : idx
            })
        },
        changeXq(data){
            dispatch({
                type : 'changeXq',
                obj : data
            })
        }
    }
  })(Xht);